/* Short Description:
 *   Some basic timer classes.
 */

#ifndef UTILITY__TIMER_HH__
#define UTILITY__TIMER_HH__

#include <sys/time.h> // used by Timer
#include <sys/times.h> // used by TickTimer

#include <inttypes.h>

#include <iostream>
#include <string>

namespace Utility
{
  class Timer
  {
  public:
    Timer ();
    
    void Reset ();
    uint64_t Delta ();
    uint64_t PerSecond ();
    
    uint64_t Value ();
    
  private:
    timeval m_start;
  };
  
  class TickTimer
  {
  public:
    TickTimer ();
    
    void Reset ();
    uint64_t Delta ();
    uint64_t PerSecond ();
    
    uint64_t Value ();
    
  private:
    struct tms m_times;
  };
  
  class TimebaseTimer
  {
  public:
    TimebaseTimer ();
    
    void Reset ();
    uint64_t Delta ();
    uint64_t PerSecond ();
    
    uint64_t Value ();
    
  private:
    
    uint64_t start_tick;
  };

  template <typename T>
  class AutoTimer
  {
  public:
    AutoTimer (const std::string& i_text) {
      m_text = i_text;
    }
    ~AutoTimer () {
      std::cout << ">> AutoTimer: \"" << m_text
		<< "\" took: " << m_timer.Delta () << " us" << std::endl;
    }
    
    uint64_t Delta () {
      return m_timer.Delta ();
    }
    uint64_t PerSecond () {
      return m_timer.PerSecond ();
    }
    
  private:
    T m_timer;
    std::string m_text;
  };
  
} // end namespace utility

#endif // UTILITY__TIMER_HH__
